mapper.insert:
INSERT INTO DEVICE
(
    OLD_DEVICE_ID
  , EMP_NUM
  , USAGE_DIVISION
  , USAGE_PURPOSE
  , DEVICE_TYPE
  , MODEL_NAME
  , MANUFACTURE_DATE
  , CPU_SPEC
  , MEMORY_SIZE
  , STORAGE_INFO
  , OPERATING_SYSTEM
  , SCREEN_SIZE
  , DEVICE_STATUS_CODE
  , PURCHASE_DATE
  , RETURN_DATE
  , REMARKS
  , CREATE_DATETIME
  , CREATE_USER
) VALUES (
  #{oldDeviceId}
  , #{empNum}
  , #{usageDivision}
  , #{usagePurpose}
  , #{deviceTypeCode}
  , #{modelName}
  , #{manufactureDate}
  , #{cpuSpec}
  , #{memorySize}
  , #{storageInfo}
  , #{operatingSystem}
  , #{screenSize}
  , '01'
  , #{purchaseDate}
  , #{returnDate}
  , #{remarks}
  , NOW()
  , 100010
)

mapper.update:
UPDATE DEVICE
SET
    OLD_DEVICE_ID = #{oldDeviceId}
  , EMP_NUM = #{empNum}
  , USAGE_DIVISION = #{usageDivision}
  , USAGE_PURPOSE = #{usagePurpose}
  , ARCHIVE_LOCATION = #{archiveLocation}
  , DEVICE_TYPE = #{deviceTypeCode}
  , MANUFACTURER_CODE  = #{manufacturerCode}
  , MODEL_NAME = #{modelName}
  , MANUFACTURE_DATE = #{manufactureDate}
  , CPU_SPEC = #{cpuSpec}
  , MEMORY_SIZE = #{memorySize}
  , STORAGE_INFO = #{storageInfo}
  , OPERATING_SYSTEM = #{operatingSystem}
  , SCREEN_SIZE = #{screenSize}
  , GPU_SPEC = #{gpuSpec}
  , DEVICE_STATUS_CODE = #{deviceStatusCode}
  , APPROVAL_STATUS_CODE = #{approvalStatusCode}
  , PURCHASE_DATE = #{purchaseDate}
  , RETURN_DATE = #{returnDate}
  , REMARKS = #{remarks}
  , UPDATE_DATETIME = NOW()
  , UPDATE_USER = 100010
WHERE DEVICE_NUM = #{deviceNum};
