mapper.update:
UPDATE DEVICE
SET
    OLD_DEVICE_ID = #{oldDeviceId}
  , EMP_NUM = #{empNum}
  , USAGE_DIVISION = #{usageDivisionCode}
  , USAGE_PURPOSE = #{usagePurpose}
  , ARCHIVE_LOCATION = #{archiveLocation}
  , DEVICE_TYPE = #{deviceTypeCode}
  , MODEL_NAME = #{modelName}
  , MANUFACTURER  = #{manufacturer}
  , MANUFACTURE_DATE = #{manufactureDate}
  , CPU_SPEC = #{cpuSpec}
  , MEMORY_SIZE = #{memorySize}
  , STORAGE_INFO = #{storageInfo}
  , OPERATING_SYSTEM = #{operatingSystem}
  , SCREEN_SIZE = #{screenSize}
  , GPU_SPEC = #{gpuSpec}
  , DEVICE_STATUS_CODE = #{deviceStatusCode}
  , APPROVAL_STATUS_CODE = #{approvalStatusCode}
  , PURCHASE_DATE = #{purchaseDate}
  , RETURN_DATE = #{returnDate}
  , REMARKS = #{remarks}
  , UPDATE_DATETIME = NOW()
  , UPDATE_USER = #{updateUser}
WHERE DEVICE_NUM = #{deviceNum};

UPDATE DEVICE
SET
    APPROVAL_STATUS_CODE = #{approvalStatusCode}
WHERE
    DEVICE_NUM = #{deviceNum}

mapper.select:
SELECT DH.HISTORY_NUM, DH.DEVICE_NUM, DH.EMP_NUM, U.USER_NAME, CC1.CODE_NAME AS deviceStatus, CC2.CODE_NAME AS approvalStatus, DH.CHANGE_CONTENTS, DH.REASON, DH.CREATE_DATETIME, DH.CREATE_USER, D.DEVICE_TYPE, O.ORG_NAME FROM DEVICE_HISTORY DH INNER JOIN DEVICE D ON DH.DEVICE_NUM = D.DEVICE_NUM LEFT JOIN USER U ON DH.EMP_NUM = U.EMP_NUM LEFT JOIN ORGANIZATION O ON U.ORG_ID = O.ORG_ID LEFT JOIN COMMON_CODE CC1 ON DH.DEVICE_STATUS_CODE = CC1.CODE AND CC1.CATEGORY = 'C005' LEFT JOIN COMMON_CODE CC2 ON DH.APPROVAL_STATUS_CODE = CC2.CODE AND CC2.CATEGORY = 'C006'
AND DH.DEVICE_NUM = #{searchKeyword}
AND U.USER_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND O.ORG_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND DH.EMP_NUM = #{loginEmpNum}
AND U.ORG_ID = #{loginOrgId}
ORDER BY DH.CREATE_DATETIME DESC LIMIT #{size} OFFSET #{page}

SELECT DH.DEVICE_NUM, (SELECT CODE_NAME FROM COMMON_CODE C WHERE C.CATEGORY='C005' AND C.CODE = DH.DEVICE_STATUS_CODE) AS deviceStatus, (SELECT CODE_NAME FROM COMMON_CODE C WHERE C.CATEGORY='C006' AND C.CODE = DH.APPROVAL_STATUS_CODE) AS approvalStatus, DH.CREATE_DATETIME FROM ( SELECT DEVICE_NUM, DEVICE_STATUS_CODE, APPROVAL_STATUS_CODE, CREATE_DATETIME, EMP_NUM, ROW_NUMBER() OVER (PARTITION BY DEVICE_NUM ORDER BY HISTORY_NUM DESC) AS rn FROM DEVICE_HISTORY ) DH LEFT JOIN USER U ON DH.EMP_NUM = U.EMP_NUM WHERE DH.rn = 1
AND DH.EMP_NUM = #{loginEmpNum}
AND U.ORG_ID = #{loginOrgId}
AND DH.APPROVAL_STATUS_CODE = #{approvalStatusCode}
ORDER BY DH.CREATE_DATETIME DESC LIMIT #{size} OFFSET #{page}

SELECT COUNT(*) FROM DEVICE_HISTORY DH INNER JOIN DEVICE D ON DH.DEVICE_NUM = D.DEVICE_NUM LEFT JOIN USER U ON DH.EMP_NUM = U.EMP_NUM LEFT JOIN COMMON_CODE CC1 ON DH.DEVICE_STATUS_CODE = CC1.CODE AND CC1.CATEGORY = 'C005' LEFT JOIN COMMON_CODE CC2 ON DH.APPROVAL_STATUS_CODE = CC2.CODE AND CC2.CATEGORY = 'C006'
AND DH.DEVICE_NUM = #{searchKeyword}
AND U.USER_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND O.ORG_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND DH.EMP_NUM = #{loginEmpNum}
AND U.ORG_ID = #{loginOrgId}

INSERT INTO DEVICE_HISTORY
(
  DEVICE_NUM
, EMP_NUM
, DEVICE_STATUS_CODE
, APPROVAL_STATUS_CODE
, CHANGE_CONTENTS
, REASON
, CREATE_DATETIME
, CREATE_USER
)
VALUES
(
  #{deviceNum}
, #{empNum}
, #{deviceStatus}
, #{approvalStatus}
, #{changeContents}
, #{reason}
, NOW()
, #{createUser}
)

