mapper.insert:
INSERT INTO DEVICE_INFO
(
DEVICE_NAME
, CONTENTS
, REG_USER
, REG_DATE
) VALUES (
#{deviceName}
, #{contents}
, #{regUser}
, NOW()
)

mapper.update:
UPDATE DEVICE_INFO
SET DEVICE_NAME = #{deviceName}
, CONTENTS = #{contents}
, MOD_USER = #{modUser}
, MOD_DATE = NOW()
WHERE DEVICE_NUMBER = #{deviceNumber}
