mapper.select:
SELECT EMP_NUM
FROM USER
WHERE USER_ID = #{userId}

SELECT USER_ID
FROM USER
WHERE EMP_NUM = #{empNum}

SELECT EMP_NUM
FROM USER
WHERE ROLE_CODE = #{roleCode}

mapper.insert:
INSERT INTO MESSAGE
(
    RECIPIENT
    , SENDER
    , TITLE
    , CONTENT
    , MESSAGE_STATUS_CODE
    , CREATE_DATETIME
) VALUES (
    #{recipient}
    , #{sender}
    , #{title}
    , #{content}
    , #{messageStatusCode}
    , NOW()
)
