mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY N.NOTICE_NUM ASC) AS ROW_NUM , N.NOTICE_NUM , N.TITLE , CONCAT(O.ORG_NAME, '/', U.USER_NAME) AS CREATE_USER , DATE_FORMAT(N.CREATE_DATETIME, '%Y-%m-%d') AS CREATE_DATETIME FROM NOTICE N JOIN USER U ON N.CREATE_USER = U.EMP_NUM JOIN ORGANIZATION O ON U.ORG_ID = O.ORG_ID
N.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY NOTICE_NUM DESC LIMIT #{size} OFFSET #{page}

mapper.insert:
INSERT INTO NOTICE
(
    TITLE
  , CONTENT
  , CREATE_DATETIME
  , CREATE_USER
) VALUES (
    #{title}
  , #{content}
  , NOW()
  , #{createUser}
)

mapper.update:
UPDATE NOTICE
SET TITLE = #{title}
  , CONTENT = #{content}
  , UPDATE_DATETIME = NOW()
  , UPDATE_USER = #{updateUser}
WHERE NOTICE_NUM = #{noticeNum}
