mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY N.NOTICE_NUM) AS ROW_NUM, N.NOTICE_NUM, N.TITLE, N.CONTENT, CONCAT(O.ORG_NAME, '/', U.USER_NAME) AS CREATE_USER, DATE_FORMAT(N.CREATE_DATETIME, '%Y-%m-%d') AS CREATE_DATETIME FROM NOTICE N JOIN USER U ON N.CREATE_USER = U.EMP_NUM LEFT OUTER JOIN ORGANIZATION O ON U.ORG_ID = O.ORG_ID
N.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
N.CONTENT LIKE CONCAT('%', #{searchKeyword}, '%')
) AS NOTICE ORDER BY NOTICE.ROW_NUM DESC LIMIT #{size} OFFSET #{page}

mapper.insert:
INSERT INTO FILE
(
    ORIGIN_FILE_NAME
    , STORED_FILE_NAME
    , UPLOAD_DATETIME
    , NOTICE_NUM
) VALUES (
    #{originFileName}
    , #{storedFileName}
    , NOW()
    , #{noticeNum}
)

INSERT INTO NOTICE
(
    TITLE
    , CONTENT
    , CREATE_DATETIME
    , CREATE_USER
) VALUES (
    #{title}
    , #{content}
    , NOW()
    , #{createUser}
)

mapper.update:
UPDATE NOTICE
SET TITLE = #{title}
  , CONTENT = #{content}
  , UPDATE_DATETIME = NOW()
  , UPDATE_USER = #{updateUser}
WHERE NOTICE_NUM = #{noticeNum}

UPDATE FILE
SET IS_DELETED = 'Y'
WHERE FILE_NUM = #{fileNum}
