mapper.select:
SELECT N.NOTICE_NUM , N.TITLE , CONCAT(O.ORG_NAME, '/', U.USER_NAME) AS CREATE_USER , N.CREATE_DATETIME FROM NOTICE N, USER U, ORGANIZATION O WHERE N.CREATE_USER = U.EMP_NUM AND U.ORG_ID = O.ORG_ID
AND N.TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY NOTICE_NUM DESC LIMIT #{size} OFFSET #{page}

mapper.insert:
INSERT INTO NOTICE
(
TITLE
, CONTENT
, CREATE_DATETIME
, CREATE_USER
) VALUES (
#{title}
, #{content}
, NOW()
, #{createUser}
)

mapper.update:
UPDATE NOTICE
SET TITLE = #{title}
, CONTENT = #{content}
, UPDATE_DATETIME = NOW()
, UPDATE_USER = #{updateUser}
WHERE NOTICE_NUM = #{noticeNum}
